// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

//import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract PredictorsApp {

    /* ////////////////////////////////// */
    /* ////////// DEKENLER /////////// */
    /* ////////////////////////////////// */
    address private owner; // Contract sahibinin adresini tutmak iin bir deiken oluturuyoruz.
    uint256 public userCount; // Kullanc saysn tutmak iin bir deiken oluturuyoruz. Backend'e saklayacaz
    uint256 public postCount; // Post saysn tutmak iin bir deiken oluturuyoruz. Backend'e saklayacaz
    address public admin; // Admin adresini tutmak iin bir deiken oluturuyoruz.
    WinnerLoserState public state; // WinnerLoserState struct'n manipule etmek iin bu deikeni kullanacaz.

    /* ////////////////////////////////// */
    /* //////////// ENUMLAR ///////////// */
    /* ////////////////////////////////// */

    enum SIDE {LEFT,RIGHT} // LEFT/RIGHT enum tipini oluturuyoruz.

    /* ////////////////////////////////// */
    /* ///////// STRUCT YAPILARI //////// */
    /* ////////////////////////////////// */

    //Kullanc bilgilerini tutmak iin User struct' oluturuyoruz.
    struct User {
        uint256 id; // Kullancnn ID'sini tutuyoruz.
        string fullName; // Kullancnn adn ve soyadn tutuyoruz.
        uint balance; //Kullancnn bakiyesi
        address walletAddress; //Kullancnn czdan adresini tutuyoruz.
        uint256 registerDate; //Kullancnn kayt olduu tarihi tutuyoruz.
        bool isVerified; //Kullancnn dorulanp dorulanmadn tutuyoruz.
        uint256[] partipicatedPost; //Kullanicinin Katildigi (DER) Postlar - Int Array (Kendi postuna katlamaz)
        uint256[] allPredictions; //Kullanicinin Tum Postlarinn ID'leri - Int Array
        uint256 predictionSuccesful; //Kullanici Basarili Sonuclanan Post Sayisi
    }

    //Post bilgilerini tutmak iin Post struct' oluturuyoruz.
    struct Post {
        uint256 id; // Postun ID'sini tutuyoruz.
        string postContent; // Postun ieriini tutuyoruz.
        uint256 postBet; // Kahinin postu olutururken yatrd bet miktarn tutuyoruz.
        uint256 postDate; // Postun oluturulma tarihini tutuyoruz.
        uint256 postEndDate; // Postun biti tarihini tutuyoruz.
        address[] postParticipants; //Post katilimcileri - Address Array
        uint256 postBetPool; //Posta katlmclarn yatrd toplam bet miktarn tutuyoruz.
        bool postFinished; // Postun bitip bitmediini tutuyoruz.
    }

    // Kehanetin sonucunu yani hangi tarafn kazandn burada tutacaz
    struct WinnerLoserState {
        SIDE winner; // SIDE tipinde kazananlar tutuyoruz. (Left'in ve Right'n kazananlar)
        SIDE loser; // SIDE tipinde kaybedenleri tutuyoruz. (Left'in ve Right'n kaybedenleri)
    }

    /* ////////////////////////////////// */
    /* //////// MAPPING YAPILARI //////// */
    /* ////////////////////////////////// */

    mapping(address => User) public users; // Kullanclar tutmak/dndrmek iin bir mapping oluturuyoruz.
    mapping(uint256 => Post) public posts; // Postlar tutmak/dndrmek iin bir mapping oluturuyoruz.
    mapping(SIDE => uint256) public topBetsForSide; //Taraflar iin toplam yatrlan bahisler. dl datmnda kullanlacak
    mapping(address => mapping(uint256 => SIDE)) public sideByUser; // Kullancnn setii taraf tutuyoruz.
    mapping(address => mapping(SIDE => uint256)) public betAmountByUser; //Kullancnn tarafa yatrd bahis

    // Post bet miktarn wei cinsinden tutuyoruz
    mapping(uint256 => uint256) public postBet;
    mapping(uint256 => uint256) public postBetPool;


    /* ////////////////////////////////// */
    /* ///////// EVENT YAPILARI ///////// */
    /* ////////////////////////////////// */

    // Fonksiyonlarn durum bilgileri event ile bakalarna yayn yapyoruz
    event NewUser(uint256 id, string fullName, address walletAddress);
    event NewPost(uint256 id,string postContent,uint256 postBet,uint256 postEndDate,address postAuthor,SIDE _side);
    event PostParticipation(uint256 postId,address participant,uint256 amount,SIDE _side);
    event PostFinish(uint256 postId,address winner,uint256 totalBet,uint256 authorReward);
    event BalanceWithdrawn(address walletAddress,uint256 amount);

    /* ////////////////////////////////// */
    /* /////////// CONSTRUCTOR ////////// */
    /* ////////////////////////////////// */

    // Balangta ilklenmesi iin constructor fonksiyonu oluturuyoruz, kullanc ve post saylarn sfra ayarlyoruz.
    constructor() {
        owner = msg.sender;

       /*  userCount = userCount;
        postCount = postCount; */
    }

    /* ////////////////////////////////// */
    /* /////////// MODIFIERLAR ////////// */
    /* ////////////////////////////////// */

    // Sadece adminin kullanabilecei fonksiyonlar belirlemek iin bir modifier oluturuyoruz.
    modifier onlyOwner() {
        require(msg.sender == owner, "Sadece hesap sahibi degistirebilir!");
        _;
    }

    /* ////////////////////////////////// */
    /* /////// EXECUTE FONKSIYONLAR ///// */
    /* ////////////////////////////////// */
    // UI'da kullanc deiirse yeni sahiplii setOwner ile vereceiz
    function setOwner(address _newOwner) external onlyOwner {  
        owner = _newOwner;
    }

    // Kullanc oluturma fonksiyonu
    function registerUser(string memory _fullName) public {
        require(bytes(_fullName).length > 0, "Full name is required");

        require(!isUser(msg.sender) , "Bu kullanici zaten var!"); //Kullanc yoksa devam et
        userCount+=1;

        users[msg.sender] = User({
            id: userCount,
            fullName: _fullName,
            balance: 0,
            walletAddress: msg.sender,
            registerDate: block.timestamp,
            isVerified: false, //dogrulanmis mi
            partipicatedPost: new uint256[](0), //diger katildigi tahminlerin/postlarn indexi
            allPredictions: new uint256[](0), //oluturduu postlarn/tahminlerin indexi
            predictionSuccesful: 0 //Kullancnn basarili tahmin saysn tutuyoruz
        });

        emit NewUser(userCount, _fullName, msg.sender);
    }
    // Post oluturma fonksiyonu
    function createPost(string memory _postContent,uint256 _postBet,uint256 _postEndDate, SIDE _side) public {
        require(isUser(msg.sender) , "Post olusturmak icin kullanici olmalisiniz!"); //Kullanc yoksa devam et
        require(bytes(_postContent).length > 0, "Post content is required");
        require(_postBet > 0, "Bet amount must be greater than 0");
        //require(_postEndDate > block.timestamp, "End date must be in the future");
        postCount+=1;

        //Post yaratldnda ilk katlmc olarak post sahibini ekliyoruz.
        address[] memory initialParticipants = new address[](1);
        initialParticipants[0] = msg.sender;

        //Seilen taraf post ID sine gre kullancnn setii tarafa mapliyoruz.
        sideByUser[msg.sender][postCount] = _side;

        posts[postCount] = Post({
            id: postCount, //Postun ID si
            postContent: _postContent, //Post icerigi
            postBet: _postBet, //Postun degeri
            postDate: block.timestamp,
            postEndDate: _postEndDate,
            postParticipants: initialParticipants, // lk katlmc olarak sadece gndereni ekle
            postBetPool: _postBet, //Postun toplam havuzu. Ilk yaratildiginda kahin betini post havuzuna ekledik
            postFinished: false // Post bitti mi
            //taraf: sideByUser[msg.sender][postCount] //Kahinin post'ta seecei taraf structa atadk
        });

        //Kahinin evet veya hayr seeneine dedii miktar ve hangi tarafn setiini tutan mappingi gncelleiyoruz.
        betAmountByUser[msg.sender][_side] += _postBet; //Kahin'in odedigi miktari guncelledik
        topBetsForSide[_side] += _postBet;

        //Kullanicinin toplam tahmin sayisini guncelliyoruz
        users[msg.sender].allPredictions.push(postCount);

        emit NewPost(postCount, _postContent, _postBet, _postEndDate, msg.sender, _side);
    }

    // Dier kullanclarn bir posta katlm fonksiyonu
    function participateInPost( uint256 _postId, SIDE _side, uint256 _amount ) public {
        require(_postId <= postCount && _postId > 0, "Invalid post ID");
        //require(posts[_postId].postEndDate > block.timestamp, "Post has ended.");
        require(_amount > 0, "Amount must be greater than 0");
        //Post sahibi kendi postuna bahis yapamaz
        require(posts[_postId].postParticipants[0] != msg.sender,
            "Kendi kehanetine katilim yapamazsin!");

        //Evet veya Hayr seeneinin toplam toplam havuzuna amount u ekliyoruz.
        topBetsForSide[_side] += _amount;
        posts[_postId].postBetPool += _amount; //Postun havuzunu gncelliyoruz.

        //Kahin'in evet veya hayr seeneine dedii miktar gncelleiyoruz.
        //rn: Kahin(msg.sender) "Evet/Hayr" seeneine _amount miktar deme ekledi.
        betAmountByUser[msg.sender][_side] += _amount; //Kahin'in odedigi miktari guncelledik


        sideByUser[msg.sender][_postId] = _side; //Kullancnn postID'ye gre tarafn gncelledik
        //posts[_postId].taraf = _side;//Post'un taraf havuzunu arttrdk

        posts[_postId].postParticipants.push(msg.sender); // Katlan kullancnn czdan adresini katilimcilar dizisine ekledik

        //Kullancnn dier katld postlar gncelliyoruz.
        users[msg.sender].partipicatedPost.push(_postId);

        emit PostParticipation(_postId, msg.sender, _amount, _side);
    }

    // Etkinligin kazanani belirleyen fonksiyon.
    // winner ile 0(LEFT) veya 1(RIGHT) alacaz. loser ile 0(LEFT) veya 1(RIGHT) alacaz
    function postResults( uint256 _postId, SIDE _winner, SIDE _loser) external payable {
        //require(predictor == msg.sender, "Kahin degilsiniz!"); //Kahin olup olmadn kontrol ediyoruz.
        require(posts[_postId].postFinished == false, "Kehanet bitmedi!"); //Kehanetin bitmediyse devam et, bittiyse rapor oluturma.

        //Hangi tarafn kazandn struct'a bildiriyoruz
        state.winner = _winner; //Param ile gelen winner'i struct'taki winner'a atiyoruz.
        state.loser = _loser; //Param ile gelen loser'i struct'taki loser'a atiyoruz.
        posts[_postId].postFinished = true; //Kehanetin bittigini belirtiyoruz.


        //Kazananlar listelemek iin for kullanacaz. 
        //Her dngde kullancnn state(winner) durumunu kontrol edeceiz.
        // Side durumunu ve katlmclar almak iin geici diziler oluturuyoruz
        SIDE[] memory participantsSides = new SIDE[](posts[_postId].postParticipants.length); 
        address[] memory participantsAddresses = posts[_postId].postParticipants; 

        uint totalBetPool = posts[_postId].postBetPool;


        for (uint256 i = 0; i < participantsAddresses.length; i++) {
            address participant = participantsAddresses[i];
            participantsSides[i] = sideByUser[participant][_postId];

            if(participantsSides[i] == state.winner){  // Katlmcnn adresi  = winner ise

                users[participant].predictionSuccesful +=1; //Kazananlarin basarili tahmin degerini guncelledik

                // Odul miktarini hesapla (kazananlarin toplam bahis havuzunun yuzdesi)
                uint participantBet = betAmountByUser[participant][participantsSides[i]]; //Yatrd sermaye

                //(Yatrd bahis * Havuzdaki top miktar) /toplam kazanan says
                uint rewardAmount = (participantBet * totalBetPool) / topBetsForSide[state.winner];
                users[participant].balance += rewardAmount;
                //payable(users[participant]).transfer(users[participant].balance);

                //uint256 sermaye = betAmountByUser[msg.sender][state.winner];

                // KAZANC + (no diyenlerin sayisi * sermaye) / KAZANAN KISI SAYISI
                // uint256 kazanc = sermaye + (topBetsForSide[state.loser] * sermaye) / topBetsForSide[state.winner];

            }   
        }
    }

    /* //withdrawBalance
    function withdrawBalance(uint amount) external nonReentrant {
        require(isUser(msg.sender), "Kullanici bulunamadi!");

        uint balance = users[msg.sender].balance;
        require(balance >= amount, "Yetersiz bakiye!");

        users[msg.sender].balance -= amount;

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed.");

        emit BalanceWithdrawn(msg.sender, amount);
    } */

    /* //////////////////////////////////// */
    /* //////// QUERY FONKSIYONLAR //////// */
    /* //////////////////////////////////// */

    //Kullanc var olup olmadn kontrol ediyoruz. True yada False dnmesini bekliyoruz.
    function isUser(address _walletAddress) public view returns (bool) {
        return users[_walletAddress].walletAddress != address(0); // Adress verdiimiz kullancnn adresi 0'a eit deilse true dndryoruz.
    }

    // Kullancnn ad ve soyadn eken fonksiyon
    function getUserFullName(address _userAddress) public view returns (string memory) {
        return string(abi.encodePacked(users[_userAddress].fullName)); 
    }

    //Kullanc bilgilerini ekme fonksiyonu
    function getUserDetails(address _walletAddress) external view returns ( 
        uint256, 
        string memory, 
        uint, 
        address, 
        uint256, 
        bool,
        uint256[] memory, 
        uint256[] memory, 
        uint256 
        )
        {
        require(isUser(_walletAddress), "Kullanici bulunamadi!"); //Kullanc yoksa devam et

        User memory user = users[_walletAddress]; 

        return (
            user.id,
            user.fullName,
            user.balance,
            user.walletAddress,
            user.registerDate,
            user.isVerified,
            user.partipicatedPost,
            user.allPredictions,
            user.predictionSuccesful
        );
    }

    //Post bilgilerini ekme fonksiyonu
    function getPostDetails(uint256 _id) external view returns ( 
        uint256, 
        string memory, 
        uint256, 
        uint256, 
        uint256, 
        address[] memory, 
        uint256, 
        bool 
        ) {
        require(posts[_id].id != 0, "Post bulunamadi!"); // Post varsa devam et

        Post memory post = posts[_id];

        return (
            post.id,
            post.postContent,
            post.postBet,
            post.postDate,
            post.postEndDate,
            post.postParticipants,
            post.postBetPool,
            post.postFinished
        );


    }

    // Postlar aktif/pasif durumuna gre listeleme fonksiyonu (Parametre 0/1 alr)
    function getPostByStatus(bool _status) external view returns (Post[] memory) {
    uint256 count = 0;

    // Tm postlar dngye aln ve belirtilen durumu salayanlar sayn
    for (uint256 i = 1; i <= postCount; i++) {
        if (posts[i].postFinished == _status) {
            count++;
        }
    }

    // Belirtilen durumu salayan postlar saklamak iin dinamik bir dizi oluturun
    Post[] memory postsWithStatus = new Post[](count);

    // Tm postlar tekrar dngye aln ve belirtilen durumu salayanlar diziyi ekleyin
    uint256 index = 0;
    for (uint256 i = 1; i <= postCount; i++) {
        if (posts[i].postFinished == _status) {
            postsWithStatus[index] = posts[i];
            index++;
        }
    }

    return postsWithStatus;
}


    function getOwner() external view returns(address) {
         return owner;
    }

    function getUser(address _walletAddress) external view returns (User memory) {
        require(isUser(_walletAddress), "Kullanici bulunamadi!");
        return users[_walletAddress];
    }

    function getPost(uint _id) external view returns(Post memory) {
        require(posts[_id].id != 0, "Post does not exist!");
        return posts[_id];
    }

    function getUserCount() external view returns (uint) {
        return userCount;
    }

    function getPostCount() external view returns (uint) {
        return postCount;
    }

    // Post bet miktarn ondalkl sayya dntrerek grntlemek iin bir fonksiyon
    function getPostBet(uint256 _postId) external view returns (uint256) {
        // Bet miktarn ondalkl sayya dntrmek iin bir arpan kullanabilirsiniz.
        // Bu rnekte 1 ETH'in 10^18 wei olduunu varsayalm.
        uint256 betMultiplier = 10**18;
        return postBet[_postId] / betMultiplier;
    }

    // Post bet pool miktarn ondalkl sayya dntrerek grntlemek iin bir fonksiyon
    function getPostBetPool(uint256 _postId) external view returns (uint256) {
        // Bet miktarn ondalkl sayya dntrmek iin bir arpan kullanabilirsiniz.
        // Bu rnekte 1 ETH'in 10^18 wei olduunu varsayalm.
        uint256 betMultiplier = 10**18;
        return postBetPool[_postId] / betMultiplier;
    }


}
